  Porcentaje
}

enum PartyToPartyRelationshipTypeValues {
  Ac
  Acdir
}

type Query {
  # Consulta una persona por su identificador único
  crPartyReferenceDataDirectoryEntry(partyReferenceDataDirectoryId: String!): CrPartyReferenceDataDirectoryEntry!
  bqDemographics(partyReferenceDataDirectoryId: String): BQDemographic
  bqReference(partyReferenceDataDirectoryId: String): BQReference
  bqAssociations(partyReferenceDataDirectoryId: String, associationId: String): [BQAssociation]
}

type CrPartyReferenceDataDirectoryEntry {
  partyReference: Party
  partyType: PartyTypeValues
  directoryEntryDateType: DirectoryEntryDateTypeValues
  directoryEntryDate: DateTime
}

type BQDemographic {
  incomeRange: IncomeRange
  gender: Gender
  civilStatus: CivilStatus
  socioEconomicClassification: SocioEconomicClassification
  educationLevel: EducationLevel
  profession: Profession
  employmentStatus: EmploymentStatus
}

type BQReference {
  partyIdentifications: [PartyIdentification]
  partyRole: PartyRole
  economicActivity: EconomicActivities
  residenceCountry: Country
  customerSinceDate: DateTime
  partyFullLegalName: String
  residencyStatusIndicator: Indicator
  preferredContactMethod: PreferredContactMethod
  emailAddress: [ContactPoint]
  society: Society
  organisationDates: [OrganisationDate]
  headOfficeIndicator: Indicator
  foreignHeadOfficeIndicator: Indicator
  headOfficeCountry: Country
  partyLegalStructure: PartyLegalStructure
  organisationType: OrganisationType
  birthDate: DateTime
  nationalityCountry: Country
  birthCountry: Country
  personBusiness: PersonBusiness
  phoneAddresses: [ContactPoint]
  partyAddresses: [PostalAddress]
  pymeIndicator: Indicator
}

type BQAssociation {
  associateReference: Party
  associateType: PartyToPartyRelationship
  associationNumber: String
  associationValidityIndicator: Indicator
  associationValidFromDate: DateTime
  associationValidToDate: DateTime
  shareHolderParticipation: ShareHolderParticipation
}

type Party {
  partyId: String
  partyIdentification: PartyIdentification
  partyIdentificationExtension: String
  partyNames: [PartyName]
  partyType: PartyTypeValues
}

type PartyIdentification {
  partyIdentificationType: PartyIdentificationTypeValues
  partyIdentification: Identifier
}

type Identifier {
  identifierValue: String
}

type PartyName {
  partyNameType: PartyNameTypeValues
  partyName: String
}

type IncomeRange {
  incomeRangeType: IncomeRangeTypeValues
  incomeRangeTypeDescription: String
}

type Gender {
  genderType: GenderTypeValues
  genderTypeDescription: String
}

type CivilStatus {
  civilStatusType: CivilStatusTypeValues
  civilStatusTypeDescription: String
}

type SocioEconomicClassification {
  socioEconomicClassificationType: SocioEconomicClassificationTypeValues
  socioEconomicClassificationTypeDescription: String
}

type EducationLevel {
  educationLevelType: EducationLevelTypeValues
  educationLevelTypeDescription: String
}

type Profession {
  professionType: ProfessionTypeValues
  professionTypeDescription: String
}

type EmploymentStatus {
  employmentStatusType: EmploymentStatusTypeValues
  employmentStatusTypeDescription: String
}

type PartyRole {
  partyRoleType: PartyRoleTypeTypeValues
  partyRoleTypeDescription: String
}

type EconomicActivities {
  primaryEconomicActivity: EconomicActivity
  secondaryEconomicActivity: EconomicActivity
}

type EconomicActivity {
  economicActivityType: EconomicActivityTypeValues
  economicActivityTypeDescription: String
}

type Country {
  countryType: CountryTypeValues
  countryTypeDescription: String
}

type PreferredContactMethod {
  preferredContactMethodType: PreferredContactMethodTypeValues
  preferredContactMethodTypeDescription: String
}

type ContactPoint {
  electronicAddress: ElectronicAddress
  phoneAddress: PhoneAddress
}

