import React, { useState } from 'react';
import { buildSchema, isObjectType, isScalarType, isEnumType, isNonNullType, isListType } from 'graphql';

const schemaSDL = `
  type Query {
    crPartyReferenceDataDirectoryEntry(partyReferenceDataDirectoryId: String!): CrPartyReferenceDataDirectoryEntry!
    bqDemographics(partyReferenceDataDirectoryId: String): BQDemographic
    bqReference(partyReferenceDataDirectoryId: String): BQReference
    bqAssociations(partyReferenceDataDirectoryId: String, associationId: String): [BQAssociation]
  }

  type CrPartyReferenceDataDirectoryEntry {
    partyReference: Party
    partyType: PartyTypeValues
    directoryEntryDateType: DirectoryEntryDateTypeValues
    directoryEntryDate: DateTime
  }

  type Party {
    partyId: String
    partyIdentification: PartyIdentification
    partyIdentificationExtension: String
    partyNames: [PartyName]
    partyType: PartyTypeValues
  }

  type PartyIdentification {
    partyIdentificationType: PartyIdentificationTypeValues
    partyIdentification: Identifier
  }

  type Identifier {
    identifierValue: String
  }

  scalar DateTime

  enum PartyTypeValues {
    Persona
    Empresa
  }

  enum DirectoryEntryDateTypeValues {
    FechaCreacion
    FechaModificacion
  }
`;

// Helper para "desenvolver" tipos no null o listas y obtener el tipo base
function unwrapType(type) {
  if (isNonNullType(type) || isListType(type)) {
    return unwrapType(type.ofType);
  }
  return type;
}

// Componente recursivo para renderizar los campos con checkboxes
function FieldSelector({ schema, type, path, selectedFields, setSelectedFields }) {
  if (!isObjectType(type)) return null;

  const fields = type.getFields();

  const toggleField = (fieldName) => {
    const fullPath = [...path, fieldName].join('.');
    setSelectedFields((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(fullPath)) newSet.delete(fullPath);
      else newSet.add(fullPath);
      return newSet;
    });
  };

  return (
    <ul style={{ listStyle: 'none', paddingLeft: '1em', borderLeft: '1px solid #ccc' }}>
      {Object.values(fields).map((field) => {
        const fieldType = unwrapType(field.type);
        const fullPath = [...path, field.name].join('.');
        const isSelected = selectedFields.has(fullPath);

        return (
          <li key={fullPath}>
            <label>
              <input
                type="checkbox"
                checked={isSelected}
                onChange={() => toggleField(field.name)}
              />
              {field.name} ({fieldType.name || fieldType.toString()})
            </label>
            {/* Renderizar subcampos si es objeto y seleccionado */}
            {isSelected && isObjectType(fieldType) && (
              <FieldSelector
                schema={schema}
                type={fieldType}
                path={[...path, field.name]}
                selectedFields={selectedFields}
                setSelectedFields={setSelectedFields}
              />
            )}
          </li>
        );
      })}
    </ul>
  );
}

// Generar query a partir de selección
function generateQuery(schema, selectedFields) {
  const queryType = schema.getQueryType();
  if (!queryType) return '';

  const fields = queryType.getFields();

  // Agrupar campos seleccionados por primer nivel
  const grouped = {};
  selectedFields.forEach((fullPath) => {
    const parts = fullPath.split('.');
    const first = parts[0];
    const rest = parts.slice(1).join('.');
    if (!grouped[first]) grouped[first] = [];
    if (rest) grouped[first].push(rest);
  });

  function genFields(type, paths) {
    if (!paths.length) return '';
    const typeObj = schema.getType(type.name);
    if (!isObjectType(typeObj)) return '';

    const fields = typeObj.getFields();

    return paths
      .map((p) => {
        if (!p) return null;
        const parts = p.split('.');
        const fieldName = parts[0];
        const rest = parts.slice(1).join('.');
        const field = fields[fieldName];
        if (!field) return null;
        const fieldType = unwrapType(field.type);
        const subfields = rest ? [rest] : [];
        if (isObjectType(fieldType)) {
          return `${fieldName} {\n${genFields(fieldType, subfields)}\n}`;
        } else {
          return fieldName;
        }
      })
      .filter(Boolean)
      .join('\n');
  }

  const queryFields = Object.entries(grouped)
    .map(([fieldName, subPaths]) => {
      const field = fields[fieldName];
      if (!field) return null;
      const fieldType = unwrapType(field.type);
      if (isObjectType(fieldType)) {
        return `${fieldName} {\n${genFields(fieldType, subPaths)}\n}`;
      } else {
        return fieldName;
      }
    })
    .filter(Boolean)
    .join('\n');

  return `query {\n${queryFields}\n}`;
}

export default function GraphQLCheckboxBuilder() {
  const [selectedFields, setSelectedFields] = useState(new Set());

  const schema = React.useMemo(() => buildSchema(schemaSDL), []);

  return (
    <div style={{ fontFamily: 'monospace', fontSize: 14, maxWidth: 700, margin: 'auto' }}>
      <h3>Selecciona campos para tu Query</h3>
      <FieldSelector
        schema={schema}
        type={schema.getQueryType()}
        path={[]}
        selectedFields={selectedFields}
        setSelectedFields={setSelectedFields}
      />
      <h3>Query generada</h3>
      <pre>{generateQuery(schema, selectedFields)}</pre>
    </div>
  );
}
